<template>
  <div class="navbar">
    <span class="title">Панель управления звонками</span>
    <SlideCheckbox v-model="senderStarted"></SlideCheckbox>
  </div>
</template>

<script>
import SlideCheckbox from './SlideCheckbox';

export default {
  components: {
    SlideCheckbox,
  },
  computed: {
    senderStarted: {
      get() {
        return this.$store.state.senderStarted;
      },
      set(e) {
        this.$store.commit('switch_sender', e);
      },
    },
  },
  data() {
    return {};
  },
  methods: {},
};
</script>

<style scoped>
.navbar {
  display: flex;
  justify-content: space-between;
  align-items: center;
  background: var(--dark-blue);
  padding: 0 80px 0 50px;
}

.title {
  color: var(--white);
  font-size: 30px;
  font-weight: bold;
}

>>> .slidecheckbox {
  cursor: pointer;
}

>>> .slidecheckbox-button {
  background-image: url('data:image/png;base64,');
  background-position: center center;
  background-size: contain;
  background-color: transparent;
  cursor: pointer;
  transform: scale(1.3);
}

>>> .slidecheckbox-button.checked {
  background-image: url('data:image/png;base64,');
  background-color: transparent;
  transform: scale(1.3);
}
</style>
